       a.RSV3,
       a.RSV4,
       a.RSV5,
       a.C_RSV1,
       a.C_RSV2,
       a.C_RSV3,
       a.C_RSV4,
       a.C_RSV5,
       a.TASK_NAME,
       a.TASK_ID,
       a.TASK_MODULE,
       b.BRNAME                                                            as ORG_NAME,
       case when c.REVISE_NUM is null then ''0'' else c.REVISE_NUM || '''' end as REVISE_NUM,
       case when d.REVIEW_NUM is null then ''0'' else d.REVIEW_NUM || '''' end as REVIEW_NUM
from CCRS_BM_TODO_TASK a
         left join GP_BM_BRANCH b on a.ORG_ID = b.BRNO
         left join (select a.P_TASK_ID, a.REPORT_CODE, a.REPORT_NAME, a.GROUP_ID, count(1) REVISE_NUM
                    from CCRS_BM_TODO_TASK a
                    where a.TASK_TYPE = ''1''
                      and a.NEXT_ACTION = ''00''
                      and a.REPORT_DATE = :"reportDate"
                    group by a.P_TASK_ID, a.REPORT_CODE, a.REPORT_NAME, a.GROUP_ID) c on a.TASK_ID = c.P_TASK_ID
         left join (select a.P_TASK_ID, a.REPORT_CODE, a.REPORT_NAME, a.GROUP_ID, count(1) REVIEW_NUM
                    from CCRS_BM_TODO_TASK a
                    where a.TASK_TYPE = ''1''
