                    group by a.P_TASK_ID, a.REPORT_CODE, a.REPORT_NAME, a.GROUP_ID) d on a.TASK_ID = d.P_TASK_ID
         left join(select distinct P_TASK_ID, GROUP_ID from CCRS_BM_TODO_TASK) e on a.TASK_ID = e.P_TASK_ID
