INSERT INTO GP_BM_INQ_CFG (DATA_ID, DATA_DATE, CORP_ID, ORG_ID, GROUP_ID, NEXT_ACTION, DATA_STATUS, DATA_FLAG, DATA_SOURCE, CHECK_FLAG, CHECK_DESC, CHECK_ERR_TYPE, DATA_VERSION, DATA_REJ_DESC, DATA_DEL_DESC, DATA_CRT_USER, DATA_CRT_DATE, DATA_CRT_TIME, DATA_CHG_USER, DATA_CHG_DATE, DATA_CHG_TIME, DATA_APV_USER, DATA_APV_DATE, DATA_APV_TIME, RSV1, RSV2, RSV3, RSV4, RSV5, REMARK, SQL_ID, SQL_STRING, DETAIL_SQL_STRING, SYSTEM) VALUES ('Ccrs_TASK_CHECK_001', null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, 'RNtE6M4JeW9o0_hBF74a-A', null, 'Ccrs_TASK_CHECK_001', 'select err.DATA_RPT_DATE,
       err.RULE_ID,
       r.RULE_TYPE,
       r.RULE_SOURCE,
       err.TABLE_NAME,
       cfg.REPORT_NAME TABLE_NAME_DESC,
       err.ORG_ID,
       err.GROUP_ID,
       r.FIELD,
       r.FIELD_NAME,
       r.RULE_MESSAGE,
       r.RULE_DESC,
       err.ERROR_COUNT,
       cfg.REVISE_FUNCID FUNC_ID,
       func.FUNCNAME FUNC_NAME,
       func.PAGEPATH FUNC_PATH
from (select ORG_ID, GROUP_ID, DATA_RPT_DATE, RULE_ID, TABLE_NAME, count(RECORD_ID) ERROR_COUNT
      from CCRS_BM_CHK_ERRORS
      group by ORG_ID, GROUP_ID, DATA_RPT_DATE, RULE_ID, TABLE_NAME) err
         left join GP_QC_RULE r on err.RULE_ID = r.DATA_ID
         left join CCRS_BM_RPT_CFG cfg on err.TABLE_NAME = cfg.REPORT_CODE
         left join GP_BM_FUNCTION func on cfg.REVISE_FUNCID = func.FUNCID
where 1 = 1
  and err.TABLE_NAME = :"tableName"
  and err.ORG_ID = :"orgId"
  and err.GROUP_ID = :"groupId"
  and err.DATA_RPT_DATE = :"dataRptDate"
  and r.RULE_TYPE = :"ruleType"
  and r.RULE_SOURCE = :"ruleSource"
order by err.DATA_RPT_DATE desc,err.ORG_ID,err.GROUP_ID,err.RULE_ID', null, 'CCRS');
